tables.table_schema:
CREATE TABLE user_feedback (
    user_id INT PRIMARY KEY,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE challenges (
    challenge_id INT PRIMARY KEY,
    challenge_name VARCHAR(255),
    challenge_type VARCHAR(50),
    difficulty_level INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    join_date DATE
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    role VARCHAR(20)
);

CREATE TABLE challenges (
    challenge_id UUID PRIMARY KEY,
    challenge_name VARCHAR(100),
    difficulty INT,
    release_date DATE
);

CREATE TABLE game_designers (
    designer_id UUID PRIMARY KEY,
    designer_name VARCHAR(50),
    company VARCHAR(50)
);
