tables.table_schema:
CREATE TABLE challenges (
    challenge_id INT,
    challenge_name VARCHAR(255),
    challenge_category VARCHAR(100),
    challenge_difficulty INT
);

CREATE TABLE competitions (
    competition_id INT,
    competition_name VARCHAR(255),
    competition_date DATE
);

CREATE TABLE Judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR(255),
    judge_email VARCHAR(255),
    judge_specialty VARCHAR(50)
);

CREATE TABLE Scores (
    score_id INT PRIMARY KEY,
    participant_id INT REFERENCES Participants(participant_id),
    challenge_id INT REFERENCES AR_Challenges(challenge_id),
    score DECIMAL(5,2)
);

CREATE TABLE judges (
    judge_id UUID PRIMARY KEY,
    judge_name VARCHAR(255),
    judge_email VARCHAR(255),
    judge_country VARCHAR(50)
);
