tables.table_schema:
CREATE TABLE AR_Experience_Logs (
  log_id INT PRIMARY KEY,
  log_date TIMESTAMP,
  user_id INT,
  ar_experience TEXT
);

CREATE TABLE UserProfiles (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(255),
  role VARCHAR(20)
);

CREATE TABLE ConfigSettings (
  setting_id INT PRIMARY KEY,
  setting_name VARCHAR(50),
  setting_value TEXT
);

CREATE TABLE user_data(
    user_id UUID PRIMARY KEY,
    user_name TEXT,
    user_email TEXT,
    user_role TEXT
);
