tables.table_schema:
CREATE TABLE PlayerInjuries (
    player_id UUID PRIMARY KEY,
    injury_description TEXT,
    recovery_status TEXT,
    recovery_time TIMESTAMP
);

CREATE TABLE InjuryLogs (
    log_id SERIAL PRIMARY KEY,
    player_id UUID,
    log_timestamp TIMESTAMP,
    log_description TEXT
);

CREATE TABLE PlayerInjuries (
    player_id INT,
    injury_type VARCHAR(50),
    injury_date DATE,
    recovery_status VARCHAR(20)
);

CREATE TABLE RecoveryProgress (
    recovery_id INT,
    player_id INT,
    recovery_date DATE,
    progress_percentage DECIMAL(5,2)
);

CREATE TABLE ARSimulation (
    simulation_id INT,
    player_id INT,
    simulation_date DATE,
    simulation_result VARCHAR(100)
);

CREATE TABLE Player (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50) NOT NULL,
    age INT,
    team_id INT
);

CREATE TABLE Team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL,
    coach_name VARCHAR(50),
    city VARCHAR(50)
);

CREATE TABLE RecoveryProgress (
    recovery_id INT PRIMARY KEY,
    injury_id INT,
    recovery_date DATE,
    recovery_notes TEXT
);
