tables.table_schema:
CREATE TABLE AR_Marketing_Experiences (
    id INT PRIMARY KEY,
    experience_name VARCHAR(255) NOT NULL,
    engagement_metric DECIMAL,
    performance_metric DECIMAL
);

CREATE TABLE ar_marketing_experiences (
    experience_id UUID PRIMARY KEY,
    experience_name VARCHAR(255),
    target_audience VARCHAR(255),
    engagement_rate DECIMAL
);

CREATE TABLE engagement_metrics (
    metric_id UUID PRIMARY KEY,
    experience_id UUID REFERENCES ar_marketing_experiences(experience_id),
    timestamp TIMESTAMP,
    clicks INTEGER,
    shares INTEGER
);

CREATE TABLE performance_metrics (
    performance_id UUID PRIMARY KEY,
    experience_id UUID REFERENCES ar_marketing_experiences(experience_id),
    timestamp TIMESTAMP,
    conversion_rate DECIMAL,
    revenue DECIMAL
);

CREATE TABLE performance_metrics (
    experience_id INT,
    engagement_rate FLOAT,
    conversion_rate FLOAT,
    session_duration INT
);

CREATE TABLE user_metrics (
    user_id INT,
    visits INT,
    conversions INT,
    engagement_score FLOAT
);

CREATE TABLE experience_data (
    experience_id INT,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE device_metrics (
    device_id INT,
    experience_id INT,
    sessions INT,
    bounce_rate FLOAT
);
