tables.table_schema:
CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    ar_preferences JSONB
);

CREATE TABLE user_settings (
    user_id INT PRIMARY KEY,
    ar_settings JSONB
);

CREATE TABLE user_preferences (
    user_id UUID PRIMARY KEY,
    ar_pref_1 BOOLEAN,
    ar_pref_2 BOOLEAN
);

CREATE TABLE user_settings (
    user_id UUID PRIMARY KEY,
    ar_setting_1 TEXT,
    ar_setting_2 TEXT
);

CREATE TABLE user_data (
    user_id UUID PRIMARY KEY,
    ar_data_1 JSON,
    ar_data_2 JSON
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  ar_theme VARCHAR(50) NOT NULL,
  ar_intensity INT,
  ar_color_scheme VARCHAR(50)
);

CREATE TABLE user_settings (
  user_id INT PRIMARY KEY,
  notification_settings BOOLEAN,
  ar_mode_enabled BOOLEAN
);

CREATE TABLE user_data (
  user_id INT PRIMARY KEY,
  ar_experience_rating FLOAT,
  ar_usage_history TEXT
);

CREATE TABLE ar_devices (
  device_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES user_data(user_id),
  device_name VARCHAR(50) NOT NULL,
  device_type VARCHAR(50)
);

CREATE TABLE ar_user_preferences (
    user_id INT PRIMARY KEY,
    preferred_theme VARCHAR(50),
    preferred_language VARCHAR(50),
    default_view_mode VARCHAR(50)
);

CREATE TABLE ar_user_settings (
    user_id INT PRIMARY KEY,
    show_notifications BOOL,
    auto_save_settings BOOL,
    sync_enabled BOOL
);

CREATE TABLE ar_device_data (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(50),
    os_version VARCHAR(50)
);

CREATE TABLE ar_session_data (
    session_id INT PRIMARY KEY,
    user_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);
