tables.table_schema:
CREATE TABLE augmented_reality_performance (
    ar_content_id UUID PRIMARY KEY,
    user_id UUID,
    performance_metrics JSONB,
    timestamp TIMESTAMP
);

CREATE TABLE augmented_reality_performance (
    ar_content_id UUID PRIMARY KEY,
    ar_content_name VARCHAR(255) NOT NULL,
    ar_performance_metrics JSONB
);

CREATE TABLE user_engagement (
    user_id UUID PRIMARY KEY,
    ar_content_id UUID REFERENCES augmented_reality_performance(ar_content_id),
    engagement_score INTEGER
);

CREATE TABLE ar_devices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(255) NOT NULL,
    device_type VARCHAR(50)
);

CREATE TABLE augmented_reality_content (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    performance_score INT
);

CREATE TABLE AR_Performance (
    id INT PRIMARY KEY,
    content_id INT,
    performance_score FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE Content_Categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE User_Details (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255),
    registration_date DATE
);
