tables.table_schema:
CREATE TABLE user_profiles (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  user_behavior JSON
);

CREATE TABLE augmented_reality_logs (
  log_id UUID PRIMARY KEY,
  user_id UUID REFERENCES user_profiles(user_id),
  action_taken VARCHAR(100),
  timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    avatar_url VARCHAR(255)
);

CREATE TABLE users (
    id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    age INT
);

CREATE TABLE user_behavior (
    user_id UUID,
    behavior VARCHAR(50),
    timestamp TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(id)
);
