tables.table_schema:
CREATE TABLE augmented_reality_creators (
    creator_id UUID PRIMARY KEY,
    creator_name VARCHAR(255) NOT NULL,
    creator_email VARCHAR(255) NOT NULL,
    creator_join_date DATE NOT NULL
);

CREATE TABLE creators (
    creator_id INT PRIMARY KEY,
    creator_name VARCHAR(50) NOT NULL,
    specialty VARCHAR(50),
    join_date DATE
);

CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(100) NOT NULL,
    creator_id INT REFERENCES creators(creator_id),
    project_description TEXT,
    project_start_date DATE,
    project_end_date DATE
);

CREATE TABLE tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) NOT NULL
);

CREATE TABLE ARCreators (
    creator_id UUID PRIMARY KEY,
    creator_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    country VARCHAR(50) NOT NULL
);

CREATE TABLE ARFeedback (
    feedback_id UUID PRIMARY KEY,
    project_id UUID REFERENCES ARProjects(project_id),
    feedback_text TEXT NOT NULL,
    rating INT NOT NULL
);

CREATE TABLE ARTags (
    tag_id UUID PRIMARY KEY,
    tag_name VARCHAR(50) NOT NULL
);
