tables.table_schema:
CREATE TABLE user_profiles (
 user_id INT PRIMARY KEY,
 first_name VARCHAR,
 last_name VARCHAR,
 email VARCHAR
);

CREATE TABLE UserProfiles (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    profile_pic_url TEXT
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    language VARCHAR(50),
    theme VARCHAR(50),
    notification_enabled boolean
);

CREATE TABLE ARExperiences (
    experience_id INT PRIMARY KEY,
    experience_name VARCHAR(100),
    description TEXT,
    category VARCHAR(50)
);

CREATE TABLE VRPlaylists (
    playlist_id INT PRIMARY KEY,
    playlist_name VARCHAR(100),
    created_by INT,
    is_public boolean
);
