tables.table_schema:
CREATE TABLE virtual_objects (
    id INT PRIMARY KEY,
    object_name VARCHAR(255),
    object_type VARCHAR(50),
    object_size INT
);

CREATE TABLE augmented_reality_apps (
    app_id INT PRIMARY KEY,
    app_name VARCHAR(255),
    app_version FLOAT,
    app_description TEXT
);

CREATE TABLE ObjectModels (
    object_id INT PRIMARY KEY,
    object_name VARCHAR(255),
    object_type VARCHAR(50),
    object_size DECIMAL(10, 2)
);

CREATE TABLE ObjectTextures (
    texture_id INT PRIMARY KEY,
    object_id INT,
    texture_data BYTEA,
    FOREIGN KEY (object_id) REFERENCES ObjectModels(object_id)
);

CREATE TABLE ObjectLocations (
    location_id INT PRIMARY KEY,
    object_id INT,
    location_x DECIMAL(10, 2),
    location_y DECIMAL(10, 2),
    location_z DECIMAL(10, 2),
    FOREIGN KEY (object_id) REFERENCES ObjectModels(object_id)
);

CREATE TABLE VirtualObjects (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  type VARCHAR(50),
  created_at TIMESTAMP
);

CREATE TABLE VirtualObjects (
  object_id INT PRIMARY KEY,
  object_name VARCHAR(50),
  object_description TEXT,
  object_type VARCHAR(20)
);

CREATE TABLE ObjectColors (
  color_id INT PRIMARY KEY,
  color_name VARCHAR(30),
  color_rgb VARCHAR(10)
);

CREATE TABLE ObjectMaterials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(30),
  material_texture TEXT,
  material_opacity FLOAT
);

CREATE TABLE ObjectSizes (
  size_id INT PRIMARY KEY,
  size_dimension VARCHAR(20),
  size_weight FLOAT
);

CREATE TABLE ObjectLocations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(50),
  location_description TEXT,
  location_coordinates VARCHAR(30)
);
