tables.table_schema:
CREATE TABLE Scenario (
  scenario_id INT PRIMARY KEY,
  scenario_name VARCHAR(50) NOT NULL,
  scenario_description TEXT
);

CREATE TABLE Simulation (
  simulation_id INT PRIMARY KEY,
  scenario_id INT REFERENCES Scenario(scenario_id),
  simulation_duration INT,
  simulation_results JSON
);

CREATE TABLE training_scenarios (
  scenario_id INT PRIMARY KEY,
  scenario_name VARCHAR(255),
  scenario_description TEXT,
  scenario_difficulty INT
);

CREATE TABLE scenario_steps (
  step_id INT PRIMARY KEY,
  scenario_id INT,
  step_description TEXT,
  step_duration INT
);

CREATE TABLE scenario_assets (
  asset_id INT PRIMARY KEY,
  step_id INT,
  asset_name VARCHAR(255),
  asset_type VARCHAR(50)
);

CREATE TABLE training_scenarios (
    scenario_id SERIAL PRIMARY KEY,
    scenario_name TEXT,
    scenario_description TEXT,
    scenario_duration INTERVAL
);

CREATE TABLE scenario_steps (
    step_id SERIAL PRIMARY KEY,
    scenario_id INT REFERENCES training_scenarios(scenario_id),
    step_description TEXT,
    step_duration INTERVAL
);

CREATE TABLE user_progress (
    user_id SERIAL PRIMARY KEY,
    scenario_id INT REFERENCES training_scenarios(scenario_id),
    step_id INT REFERENCES scenario_steps(step_id),
    progress_status TEXT
);

CREATE TABLE user_performance (
    user_id SERIAL PRIMARY KEY,
    completion_status BOOLEAN,
    completion_time TIMESTAMP
);

CREATE TABLE simulation (
    simulation_id INT PRIMARY KEY,
    simulation_name VARCHAR(255),
    description TEXT,
    duration_minutes INT
);

CREATE TABLE scenario (
    scenario_id INT PRIMARY KEY,
    scenario_name VARCHAR(255),
    scenario_description TEXT,
    difficulty_level ENUM('Beginner', 'Intermediate', 'Advanced')
);

CREATE TABLE user_account (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    password_hash VARCHAR(255)
);

CREATE TABLE simulation_scenario_mapping (
    simulation_id INT,
    scenario_id INT,
    PRIMARY KEY (simulation_id, scenario_id)
);
