tables.table_schema:
CREATE TABLE ethical_audit_ar_content (
    audit_id INT PRIMARY KEY,
    content_id INT,
    auditor_name VARCHAR(50),
    audit_date DATE
);

CREATE TABLE bias_identification (
    bias_id INT PRIMARY KEY,
    audit_id INT,
    bias_description TEXT,
    identified_date DATE
);

CREATE TABLE bias_correction (
    correction_id INT PRIMARY KEY,
    bias_id INT,
    correction_description TEXT,
    corrected_date DATE
);

CREATE TABLE auditor_feedback (
    feedback_id INT PRIMARY KEY,
    audit_id INT,
    feedback_text TEXT,
    feedback_date DATE
);

CREATE TABLE audits_table (
    audit_id UUID PRIMARY KEY,
    audit_name VARCHAR,
    audit_date DATE,
    auditor_name VARCHAR
);

CREATE TABLE content_table (
    content_id UUID PRIMARY KEY,
    content_name VARCHAR,
    content_type VARCHAR,
    content_owner VARCHAR
);

CREATE TABLE bias_table (
    bias_id UUID PRIMARY KEY,
    bias_type VARCHAR,
    bias_description TEXT,
    content_id UUID REFERENCES content_table(content_id)
);

CREATE TABLE violations_table (
    violation_id UUID PRIMARY KEY,
    violation_description TEXT,
    resolution_status VARCHAR,
    audit_id UUID REFERENCES audits_table(audit_id)
);

CREATE TABLE researchers_table (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR,
    researcher_specialization VARCHAR
);
