tables.table_schema:
CREATE TABLE consent_mechanisms (
    id INT PRIMARY KEY,
    user_id INT,
    ar_experience_id INT,
    consent_given BOOLEAN
);

CREATE TABLE ar_experiences (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    description TEXT,
    sensitive_info_protected BOOLEAN
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    name VARCHAR,
    email VARCHAR,
    phone_number VARCHAR
);

CREATE TABLE ar_experiences (
    ar_experience_id UUID PRIMARY KEY,
    ar_name VARCHAR,
    description TEXT,
    sensitive_info BOOLEAN
);
