tables.table_schema:
CREATE TABLE employee_training_progress (
    employee_id INT PRIMARY KEY,
    training_module_id INT,
    progress FLOAT,
    completion_date DATE
);

CREATE TABLE employee_performance (
    employee_id INT PRIMARY KEY,
    overall_performance_rating FLOAT,
    last_performance_review_date DATE
);

CREATE TABLE training_environment_analysis (
    analysis_id INT PRIMARY KEY,
    employee_id INT,
    training_module_id INT,
    performance_improvement_areas TEXT
);

CREATE TABLE Employee (
    employee_id INT,
    employee_name VARCHAR(50),
    department VARCHAR(50),
    hire_date DATE
);

CREATE TABLE Training (
    training_id INT,
    training_name VARCHAR(50),
    training_type VARCHAR(50),
    duration_hours INT
);

CREATE TABLE TrainingProgress (
    progress_id INT,
    employee_id INT,
    training_id INT,
    progress_percentage INT
);

CREATE TABLE PerformanceData (
    performance_id INT,
    employee_id INT,
    training_id INT,
    performance_score FLOAT
);

CREATE TABLE employee_progress (
  employee_id INT,
  training_course_id INT,
  progress INT,
  completion_date DATE
);

CREATE TABLE performance_data (
  employee_id INT,
  skill_id INT,
  performance_rating INT,
  observation_date DATE
);

CREATE TABLE employees (
  employee_id INT,
  employee_name VARCHAR(255),
  department VARCHAR(50),
  hiring_date DATE
);

CREATE TABLE skills (
  skill_id INT,
  skill_name VARCHAR(50),
  skill_description TEXT
);
