CREATE TABLE Assemblies (
	id INT PRIMARY KEY,
	name TEXT,
	component_id INT REFERENCES Components(id),
	quantity INT
);

CREATE TABLE parts (
  part_id INT PRIMARY KEY,
  part_name VARCHAR(50) NOT NULL,
  quantity INT NOT NULL,
  price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE components (
  component_id INT PRIMARY KEY,
  component_name VARCHAR(50) NOT NULL,
  part_id INT NOT NULL,
  FOREIGN KEY (part_id) REFERENCES parts(part_id)
);

CREATE TABLE warehouses (
  warehouse_id INT PRIMARY KEY,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE parts (
    part_id UUID PRIMARY KEY,
    part_name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE components (
    component_id UUID PRIMARY KEY,
    component_name VARCHAR(255) NOT NULL,
    part_id UUID REFERENCES parts(part_id) ON DELETE CASCADE
);

CREATE TABLE vendors (
    vendor_id UUID PRIMARY KEY,
    vendor_name VARCHAR(255) NOT NULL
);

CREATE TABLE inventory (
    inventory_id UUID PRIMARY KEY,
    part_id UUID REFERENCES parts(part_id) ON DELETE CASCADE,
    quantity INT NOT NULL,
    vendor_id UUID REFERENCES vendors(vendor_id) ON DELETE CASCADE
);

CREATE TABLE transactions (
    transaction_id UUID PRIMARY KEY,
    inventory_id UUID REFERENCES inventory(inventory_id) ON DELETE CASCADE,
    transaction_date TIMESTAMP NOT NULL,
    quantity INT NOT NULL
);
