tables.table_schema:
CREATE TABLE trainee_feedback (
  trainee_id INTEGER,
  feedback_text TEXT,
  rating INTEGER
);

CREATE TABLE training_experience (
  experience_id INTEGER,
  feedback_id INTEGER,
  improvement_plan TEXT
);

CREATE TABLE trainee (
  trainee_id SERIAL PRIMARY KEY,
  trainee_name VARCHAR(50) NOT NULL,
  trainee_email VARCHAR(100) UNIQUE,
  trainee_department VARCHAR(50)
);

CREATE TABLE Trainees (
    trainee_id UUID PRIMARY KEY,
    trainee_name VARCHAR(255),
    trainee_email VARCHAR(255),
    trainee_department VARCHAR(255)
);

CREATE TABLE TrainingSessions (
    session_id UUID PRIMARY KEY,
    session_date TIMESTAMP,
    session_duration INT,
    session_location VARCHAR(255)
);

CREATE TABLE trainee (
  trainee_id SERIAL PRIMARY KEY,
  trainee_name VARCHAR(50),
  trainee_email VARCHAR(50),
  registration_date DATE
);

CREATE TABLE session (
  session_id SERIAL PRIMARY KEY,
  trainee_id INT,
  session_date DATE,
  session_duration INT
);

CREATE TABLE question (
  question_id SERIAL PRIMARY KEY,
  question_text TEXT,
  session_id INT,
  is_active BOOLEAN
);

CREATE TABLE response (
  response_id SERIAL PRIMARY KEY,
  question_id INT,
  answer_text TEXT,
  is_correct BOOLEAN
);
