tables.table_schema:
CREATE TABLE troubleshooting_solutions (
    solution_id UUID PRIMARY KEY,
    title VARCHAR(100),
    description TEXT,
    category VARCHAR(50)
);

CREATE TABLE best_practices (
    practice_id UUID PRIMARY KEY,
    practice_name VARCHAR(100),
    steps TEXT,
    industry_sector VARCHAR(50)
);

CREATE TABLE TroubleshootingSolutions (
    solution_id UUID PRIMARY KEY,
    issue_description TEXT NOT NULL,
    solution_description TEXT NOT NULL
);

CREATE TABLE BestPractices (
    practice_id UUID PRIMARY KEY,
    practice_topic TEXT NOT NULL,
    practice_description TEXT NOT NULL
);

CREATE TABLE TrainingScenarios (
    scenario_id UUID PRIMARY KEY,
    scenario_name TEXT NOT NULL,
    scenario_description TEXT NOT NULL
);

CREATE TABLE UserFeedback (
    feedback_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    feedback_text TEXT NOT NULL
);

CREATE TABLE UserProfiles (
    user_id UUID PRIMARY KEY,
    user_name TEXT NOT NULL,
    user_role TEXT NOT NULL
);
