tables.table_schema:
CREATE TABLE sdg_progress (
  sdg_id INT PRIMARY KEY,
  target VARCHAR(50) NOT NULL,
  progress FLOAT NOT NULL,
  update_date DATE
);

CREATE TABLE ar_visualization (
  visualization_id INT PRIMARY KEY,
  location VARCHAR(100) NOT NULL,
  visualization_data BYTEA
);

CREATE TABLE progress_data (
    id SERIAL PRIMARY KEY,
    goal_name VARCHAR(50) NOT NULL,
    goal_description TEXT,
    progress_percentage DECIMAL
);

CREATE TABLE SustainableGoalsData (
    goal_id INT PRIMARY KEY,
    goal_name VARCHAR(255) NOT NULL,
    description TEXT,
    target_date DATE
);

CREATE TABLE ProgressData (
    progress_id INT PRIMARY KEY,
    goal_id INT,
    progress_percentage DECIMAL(5,2) NOT NULL,
    updated_on TIMESTAMP
);

CREATE TABLE goals (
    goal_id INT PRIMARY KEY,
    goal_name VARCHAR(255) NOT NULL,
    target_value INT NOT NULL,
    current_progress INT NOT NULL
);

CREATE TABLE indicators (
    indicator_id INT PRIMARY KEY,
    indicator_name VARCHAR(255) NOT NULL,
    target_goal_id INT NOT NULL,
    current_value INT NOT NULL
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL
);

CREATE TABLE visualization_tools (
    tool_id INT PRIMARY KEY,
    tool_name VARCHAR(255) NOT NULL,
    tool_type VARCHAR(50) NOT NULL,
    supported_data_sources VARCHAR(255) NOT NULL
);
