tables.table_schema:
CREATE TABLE user_consent (
    user_id INT PRIMARY KEY,
    consent_date DATE,
    consent_given BOOLEAN
);

CREATE TABLE user_consent (
    user_id INT,
    consent_type VARCHAR(50),
    consent_given_at TIMESTAMP,
    PRIMARY KEY (user_id)
);

CREATE TABLE user_consent (
    user_id UUID,
    activity_id INT,
    consent_timestamp TIMESTAMP,
    consent_given BOOLEAN
);

CREATE TABLE data_processing_activity (
    activity_id INT,
    activity_description TEXT,
    data_category TEXT,
    data_source TEXT
);

CREATE TABLE user_registration (
    user_id UUID,
    username VARCHAR(50),
    email VARCHAR(100),
    registration_date DATE
);

CREATE TABLE consent_history (
    user_id UUID,
    activity_id INT,
    consent_date DATE,
    consent_revoked BOOLEAN
);

CREATE TABLE user_personal_data (
    user_id INT PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    email VARCHAR(200) UNIQUE NOT NULL,
    consent_given BOOL DEFAULT FALSE
);

CREATE TABLE data_processing_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(100) NOT NULL,
    activity_description VARCHAR(200),
    activity_category VARCHAR(50) NOT NULL
);

CREATE TABLE data_processing_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL,
    category_description VARCHAR(200)
);

CREATE TABLE user_data_notifications (
    notification_id INT PRIMARY KEY,
    user_id INT REFERENCES user_personal_data(user_id),
    notification_message VARCHAR(250) NOT NULL,
    notification_sent_date DATE NOT NULL
);
