tables.table_schema:
CREATE TABLE audit_log (
    id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(50),
    date TIMESTAMP,
    details TEXT
);

CREATE TABLE data_access (
    id INT PRIMARY KEY,
    user_id INT,
    accessed_table VARCHAR(50),
    access_time TIMESTAMP
);

CREATE TABLE audit_logs (
  id INT PRIMARY KEY,
  user_id INT,
  action VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE user_accounts (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  role VARCHAR(50)
);

CREATE TABLE data_modifications (
  audit_id INT,
  table_name VARCHAR(50),
  operation VARCHAR(10),
  mod_timestamp TIMESTAMP
);

CREATE TABLE access_logs (
  log_id INT PRIMARY KEY,
  user_id INT,
  access_time TIMESTAMP,
  access_type VARCHAR(20)
);

CREATE TABLE audit_trail (
  event_id INT PRIMARY KEY,
  description TEXT,
  timestamp TIMESTAMP,
  user_id INT
);
