tables.table_schema:
CREATE TABLE authorized_access (
    id INT PRIMARY KEY,
    business_name VARCHAR(255),
    sensitive_data VARCHAR(255),
    access_time TIMESTAMP
);

CREATE TABLE BusinessInformation (
    id INT PRIMARY KEY,
    business_name TEXT NOT NULL,
    sensitive_data TEXT
);

CREATE TABLE AccessLogs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    activity_description TEXT
);

CREATE TABLE BusinessOwners (owner_id INT PRIMARY KEY,
  owner_name VARCHAR(100) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  password VARCHAR(50) NOT NULL
);

CREATE TABLE AccessLogs (log_id SERIAL PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  action VARCHAR(50) NOT NULL,
  user_id INT NOT NULL,
  FOREIGN KEY (user_id) REFERENCES BusinessEmployees(employee_id)
);

CREATE TABLE businesses (
    business_id INT PRIMARY KEY,
    business_name VARCHAR(255) NOT NULL,
    industry VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    password_hash VARCHAR(255) NOT NULL
);

CREATE TABLE sensitive_data (
    data_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    data_type VARCHAR(50) NOT NULL,
    data_content TEXT NOT NULL
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(20) NOT NULL
);

CREATE TABLE departments (
  dept_id INT PRIMARY KEY,
  dept_name VARCHAR(50),
  manager_id INT,
  location VARCHAR(50)
);
