tables.table_schema:
CREATE TABLE encrypted_data (
    id INT PRIMARY KEY,
    sensitive_data VARCHAR(255),
    encrypted_at_rest BOOLEAN,
    encrypted_in_transit BOOLEAN
);

CREATE TABLE user_data (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL,
  password VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE sensitive_data (
    data_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    encrypted_data VARCHAR(1024) NOT NULL
);

CREATE TABLE logs (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    log_message TEXT NOT NULL
);

CREATE TABLE encrypted_users (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE  encrypted_messages (
    message_id INT PRIMARY KEY,
    sender_id INT,
    receiver_id INT,
    message_text TEXT NOT NULL
);

CREATE TABLE encrypted_attachments (
    attachment_id INT PRIMARY KEY,
    message_id INT,
    attachment_data BYTEA NOT NULL
);

CREATE TABLE encrypted_keys (
    key_id INT PRIMARY KEY,
    encryption_key VARCHAR(100) NOT NULL,
    decryption_key VARCHAR(100) NOT NULL
);

CREATE TABLE encrypted_logs (
    log_id INT PRIMARY KEY,
    log_date TIMESTAMP NOT NULL,
    log_message TEXT
);
