tables.table_schema:
CREATE TABLE threats (
  threat_id INT PRIMARY KEY,
  threat_name VARCHAR(255),
  threat_type VARCHAR(50),
  threat_date DATE
);

CREATE TABLE attack_patterns (
  pattern_id INT PRIMARY KEY,
  pattern_name VARCHAR(255),
  pattern_description TEXT,
  pattern_severity INT
);

CREATE TABLE ThreatData (
  threat_id SERIAL PRIMARY KEY,
  threat_type VARCHAR(255) NOT NULL,
  threat_description TEXT,
  threat_date DATE
);

CREATE TABLE ThreatPatterns (
  pattern_id SERIAL PRIMARY KEY,
  pattern_name VARCHAR(255) NOT NULL,
  pattern_description TEXT,
  threat_id INT REFERENCES ThreatData(threat_id)
);

CREATE TABLE CyberAttacks (
  attack_id SERIAL PRIMARY KEY,
  attack_description TEXT,
  attack_date DATE,
  pattern_id INT REFERENCES ThreatPatterns(pattern_id)
);

CREATE TABLE Threats (
    threat_id UUID PRIMARY KEY,
    threat_name TEXT,
    threat_type TEXT,
    threat_date DATE
);

CREATE TABLE Incidents (
    incident_id UUID PRIMARY KEY,
    incident_date DATE,
    threat_id UUID REFERENCES Threats(threat_id),
    incident_details TEXT
);

CREATE TABLE Attackers (
    attacker_id UUID PRIMARY KEY,
    attacker_name TEXT,
    attacker_type TEXT
);
