tables.table_schema:
CREATE TABLE ThreatActors (
    actor_id SERIAL PRIMARY KEY,
    actor_name TEXT NOT NULL,
    country TEXT,
    primary_motive TEXT
);

CREATE TABLE ReportMetadata (
    metadata_id UUID PRIMARY KEY,
    report_id UUID REFERENCES ThreatIntelligenceReport(report_id),
    metadata_key VARCHAR(50),
    metadata_value VARCHAR(100)
);

CREATE TABLE ThreatActor (
    actor_id UUID PRIMARY KEY,
    actor_name VARCHAR(100) NOT NULL,
    actor_type VARCHAR(50)
);

CREATE TABLE ActorRelationship (
    relationship_id UUID PRIMARY KEY,
    threat_actor_id UUID REFERENCES ThreatActor(actor_id),
    related_actor_id UUID REFERENCES ThreatActor(actor_id),
    relationship_type VARCHAR(50)
);

CREATE TABLE AttackPattern (
    pattern_id UUID PRIMARY KEY,
    pattern_name VARCHAR(100) NOT NULL,
    pattern_description TEXT
);
