tables.table_schema:
CREATE TABLE threat_intelligence_feeds (
    feed_id INT PRIMARY KEY,
    feed_name VARCHAR(255) NOT NULL,
    feed_source VARCHAR(255) NOT NULL,
    threat_level ENUM('Low', 'Medium', 'High')
);

CREATE TABLE mitigation_actions (
    action_id INT PRIMARY KEY,
    action_name VARCHAR(255) NOT NULL,
    action_type ENUM('Block', 'Quarantine', 'Alert')
);

CREATE TABLE threat_feeds (
    id INT PRIMARY KEY,
    feed_name VARCHAR(50),
    feed_url TEXT
);

CREATE TABLE indicators (
    id INT PRIMARY KEY,
    indicator_name VARCHAR(50),
    indicator_type VARCHAR(20),
    threat_level INT
);

CREATE TABLE mitigation_actions (
    id INT PRIMARY KEY,
    action_name VARCHAR(50),
    action_type VARCHAR(20),
    action_description TEXT
);

CREATE TABLE indicators (
    id SERIAL PRIMARY KEY,
    indicator_value VARCHAR(100) NOT NULL,
    indicator_type VARCHAR(50) NOT NULL,
    source_feed_id INT REFERENCES threat_intelligence_feeds(id)
);

CREATE TABLE mitigation_actions (
    id SERIAL PRIMARY KEY,
    detection_id INT REFERENCES detections(id),
    action_type VARCHAR(50) NOT NULL,
    action_details TEXT
);

CREATE TABLE mitigation_logs (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    action_id INT REFERENCES mitigation_actions(id),
    log_details TEXT
);
