tables.table_schema:
CREATE TABLE ThreatAnalysis (
  analysis_id SERIAL PRIMARY KEY,
  analysis_date DATE,
  analysis_result VARCHAR(100) NOT NULL,
  sample_id INT REFERENCES MalwareSamples(sample_id)
);

CREATE TABLE malware_characteristics (
    characteristic_id INT PRIMARY KEY,
    characteristic_name VARCHAR(255),
    description TEXT,
    severity VARCHAR(50)
);

CREATE TABLE sample_characteristics (
    sample_id INT,
    characteristic_id INT,
    FOREIGN KEY (sample_id) REFERENCES malware_samples(sample_id),
    FOREIGN KEY (characteristic_id) REFERENCES malware_characteristics(characteristic_id)
);

CREATE TABLE MalwareCategories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(100) NOT NULL
);
