tables.table_schema:
CREATE TABLE threat_intelligence (
    threat_id UUID PRIMARY KEY,
    threat_name VARCHAR(100) NOT NULL,
    threat_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE sharing_data (
    share_id UUID PRIMARY KEY,
    threat_id UUID REFERENCES threat_intelligence(threat_id),
    share_date DATE,
    shared_by VARCHAR(50)
);

CREATE TABLE Threat_Information (
  threat_id INT PRIMARY KEY,
  threat_description TEXT,
  threat_source TEXT,
  threat_date DATE
);

CREATE TABLE Verified_Threats (
  verified_threat_id INT PRIMARY KEY,
  threat_id INT,
  verification_status BOOL,
  verification_date DATE
);

CREATE TABLE Threat_Sharing_Partners (
  partner_id INT PRIMARY KEY,
  partner_name TEXT,
  partner_location TEXT,
  partner_contact_info TEXT
);

CREATE TABLE ThreatActors (
    actor_id SERIAL PRIMARY KEY,
    actor_name TEXT NOT NULL,
    description TEXT,
    country TEXT
);

CREATE TABLE Incidents (
    incident_id SERIAL PRIMARY KEY,
    incident_name TEXT NOT NULL,
    description TEXT,
    incident_date DATE
);

CREATE TABLE Organizations (
    org_id SERIAL PRIMARY KEY,
    org_name TEXT NOT NULL,
    industry TEXT,
    country TEXT
);
