tables.table_schema:
CREATE TABLE operations_log (
  log_id INT PRIMARY KEY,
  operation_type VARCHAR(50),
  operator VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE hacker_info (
	hacker_id UUID PRIMARY KEY,
	hacker_name VARCHAR(50),
	stolen_data TEXT,
	document_type VARCHAR(20)
);

CREATE TABLE encryption_keys (
	key_id UUID PRIMARY KEY,
	hacker_id UUID,
	encryption_key VARCHAR(100),
	FOREIGN KEY (hacker_id) REFERENCES hacker_info(hacker_id)
);
