tables.table_schema:
CREATE TABLE identity_thieves (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  address VARCHAR(100) NOT NULL,
  social_security_number VARCHAR(20) UNIQUE
);

CREATE TABLE fake_identities (
  id SERIAL PRIMARY KEY,
  fake_name VARCHAR(50) NOT NULL,
  fake_address VARCHAR(100) NOT NULL,
  fake_social_security_number VARCHAR(20) UNIQUE
);

CREATE TABLE loans (
  loan_id SERIAL PRIMARY KEY,
  borrower_id INT NOT NULL,
  loan_amount DECIMAL(10, 2) NOT NULL,
  loan_status VARCHAR(20)
);

CREATE TABLE credit_cards (
  card_number VARCHAR(16) PRIMARY KEY,
  card_holder_name VARCHAR(50) NOT NULL,
  expiration_date DATE,
  cvv VARCHAR(3) NOT NULL
);
