tables.table_schema:
CREATE TABLE malware_activity (
    id INT PRIMARY KEY,
    activity_type VARCHAR(255),
    description TEXT,
    detected_at TIMESTAMP
);

CREATE TABLE MalwareLogs (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    malware_name VARCHAR(50),
    action_taken VARCHAR(100)
);

CREATE TABLE MalwareStatistics (
    stat_id INT PRIMARY KEY,
    malware_name VARCHAR(50),
    detection_count INT,
    last_detection_timestamp TIMESTAMP
);

CREATE TABLE malware_logs (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    log_type VARCHAR(50),
    log_details TEXT
);

CREATE TABLE malware_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    operating_system VARCHAR(50),
    last_login TIMESTAMP
);

CREATE TABLE malware_logs (
    log_id INT PRIMARY KEY,
    host_id INT NOT NULL,
    log_time TIMESTAMP,
    log_message TEXT
);

CREATE TABLE malware_analysis (
    analysis_id INT PRIMARY KEY,
    log_id INT NOT NULL,
    analysis_time TIMESTAMP,
    analysis_result TEXT
);

CREATE TABLE malware_report (
    report_id INT PRIMARY KEY,
    analysis_id INT NOT NULL,
    report_date DATE,
    report_details TEXT
);
