tables.table_schema:
CREATE TABLE malware_signatures (
    id INT PRIMARY KEY,
    signature VARCHAR(255) NOT NULL,
    pattern VARCHAR(255) NOT NULL
);

CREATE TABLE incoming_files (
    id INT PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_data BYTEA NOT NULL
);

CREATE TABLE malware_signatures (
    signature_id SERIAL PRIMARY KEY,
    signature_name VARCHAR(100) NOT NULL,
    signature_pattern TEXT NOT NULL
);

CREATE TABLE file_types (
    file_type_id SERIAL PRIMARY KEY,
    file_extension VARCHAR(10) NOT NULL,
    file_description TEXT
);

CREATE TABLE file_matches (
    match_id SERIAL PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_type_id INT REFERENCES file_types(file_type_id),
    matched_signature_id INT REFERENCES malware_signatures(signature_id)
);

CREATE TABLE MalwareSignatures (
    id INT PRIMARY KEY,
    signature_name VARCHAR(255),
    signature_pattern TEXT
);

CREATE TABLE MatchedFiles (
    file_id INT PRIMARY KEY,
    signature_id INT,
    file_name VARCHAR(255),
    matched_date DATE
);

CREATE TABLE MalwareTypes (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(255),
    threat_level INT
);

CREATE TABLE FileProperties (
    file_id INT PRIMARY KEY,
    file_size INT,
    file_path VARCHAR(255),
    upload_date TIMESTAMP
);

CREATE TABLE FileMatches (
    match_id INT PRIMARY KEY,
    file_id INT,
    signature_id INT,
    matched_score FLOAT
);
