tables.table_schema:
CREATE TABLE MalwarePropagation (
    malware_id INT PRIMARY KEY,
    malware_name VARCHAR(50),
    propagation_date DATE
);

CREATE TABLE InfectedDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    infected_date DATE,
    status VARCHAR(20)
);

CREATE TABLE Malware (
    malware_id INT PRIMARY KEY,
    malware_name VARCHAR(50) NOT NULL,
    propagation_date DATE,
    containment_status BOOLEAN
);

CREATE TABLE malware_propagation (
    malware_id INT PRIMARY KEY,
    malware_name VARCHAR(255),
    detection_date DATE,
    containment_status BOOLEAN
);

CREATE TABLE containment_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    report_description TEXT,
    report_summary TEXT
);
