tables.table_schema:
CREATE TABLE malware_behavior_patterns (
    malware_id UUID PRIMARY KEY,
    behavioral_pattern TEXT
);

CREATE TABLE malware_researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name TEXT,
    researcher_affiliation TEXT,
    researcher_email TEXT
);

CREATE TABLE malware_samples (
    sample_id INT PRIMARY KEY,
    sample_name TEXT,
    behavior_description TEXT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name TEXT,
    research_area TEXT
);

CREATE TABLE malware_files (
    file_id INT PRIMARY KEY,
    file_name VARCHAR(255),
    file_type VARCHAR(50),
    analysis_date DATE
);

CREATE TABLE malware_behaviors (
    behavior_id INT PRIMARY KEY,
    behavior_name VARCHAR(255) UNIQUE,
    behavior_description TEXT
);

CREATE TABLE MalwareBehavior (
    malware_id INT PRIMARY KEY,
    behavior_description TEXT,
    timestamp TIMESTAMP,
    severity_level INT
);

CREATE TABLE MalwareTypes (
    type_id INT PRIMARY KEY,
    malware_name VARCHAR(255),
    malware_family VARCHAR(255),
    first_appearance DATE
);

CREATE TABLE ResearcherInfo (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    researcher_email VARCHAR(255),
    affiliation VARCHAR(255)
);

CREATE TABLE MalwareAnalysis (
    analysis_id INT PRIMARY KEY,
    malware_id INT,
    analysis_report TEXT,
    analysis_timestamp TIMESTAMP
);
