tables.table_schema:
CREATE TABLE Malware_Metadata (
    malware_id INT PRIMARY KEY,
    malware_name VARCHAR(255),
    malware_type VARCHAR(50),
    detection_score FLOAT
);

CREATE TABLE malware_tags (
    tag_id UUID PRIMARY KEY,
    malware_id UUID,
    tag TEXT,
    FOREIGN KEY (malware_id) REFERENCES malware_metadata(malware_id)
);

CREATE TABLE malware_properties (
    property_id SERIAL PRIMARY KEY,
    malware_id INT REFERENCES malware_metadata(malware_id),
    property_name VARCHAR(50) NOT NULL,
    property_value TEXT
);

CREATE TABLE malware_tags (
    tag_id SERIAL PRIMARY KEY,
    malware_id INT REFERENCES malware_metadata(malware_id),
    tag_name VARCHAR(50) NOT NULL
);

CREATE TABLE classification_labels (
    label_id SERIAL PRIMARY KEY,
    label_name VARCHAR(50) NOT NULL,
    description TEXT
);
