tables.table_schema:
CREATE TABLE infected_devices (
	device_id INT PRIMARY KEY,
	device_name STRING,
	device_type STRING,
	last_seen TIMESTAMP
);

CREATE TABLE analysts (
	analyst_id INT PRIMARY KEY,
	analyst_name STRING,
	analyst_role STRING
);

CREATE TABLE malware_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    malware_type VARCHAR(255),
    analysis_result TEXT
);

CREATE TABLE infected_machines (
    machine_id INT PRIMARY KEY,
    ip_address VARCHAR(15),
    os_version VARCHAR(50),
    last_infected_date DATE
);

CREATE TABLE malware_signatures (
    signature_id INT PRIMARY KEY,
    signature_text TEXT,
    signature_type VARCHAR(50),
    created_date DATE
);

CREATE TABLE analysis_logs (
    log_id INT PRIMARY KEY,
    report_id INT,
    log_timestamp TIMESTAMP,
    log_message TEXT
);
