tables.table_schema:
CREATE TABLE malware_blacklist (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(255) NOT NULL,
    detection_method VARCHAR(255) NOT NULL,
    detection_date DATE NOT NULL
);

CREATE TABLE malware_entities (
    id INT PRIMARY KEY,
    entity_name VARCHAR(255) NOT NULL,
    description TEXT,
    detection_method VARCHAR(50)
);

CREATE TABLE malware_blacklist (
    id INT PRIMARY KEY,
    entity_id INT,
    detection_date DATE,
    detection_location VARCHAR(100)
);

CREATE TABLE malware_entities (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT
);

CREATE TABLE detection_logs (
    log_id INT PRIMARY KEY,
    detection_time TIMESTAMP,
    malware_id INT
);

CREATE TABLE malware_blacklist (
    malware_id INT PRIMARY KEY,
    name VARCHAR(255),
    type VARCHAR(50),
    description TEXT
);

CREATE TABLE MalwareBlacklist (
    id SERIAL PRIMARY KEY,
    malware_id INT,
    date_added TIMESTAMP NOT NULL,
    FOREIGN KEY (malware_id) REFERENCES MalwareEntities(id)
);

CREATE TABLE malware_entities (
    id INT PRIMARY KEY,
    entity_name VARCHAR(255) NOT NULL,
    description TEXT,
    threat_level INT
);

CREATE TABLE detection_logs (
    log_id INT PRIMARY KEY,
    entity_id INT,
    detection_time TIMESTAMP,
    detection_result VARCHAR(50)
);

CREATE TABLE blacklisted_ips (
    ip_address VARCHAR(50) PRIMARY KEY,
    reason TEXT,
    blacklisted_at TIMESTAMP
);

CREATE TABLE detection_rules (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(255) NOT NULL,
    rule_description TEXT
);

CREATE TABLE detection_statistics (
    stat_id INT PRIMARY KEY,
    date DATE,
    total_detected_entities INT
);
