tables.table_schema:
CREATE TABLE MalwareEvents (
    event_id INT PRIMARY KEY,
    event_date TIMESTAMP,
    malware_type TEXT,
    threat_level TEXT
);

CREATE TABLE MalwareEvents (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    event_date DATE,
    event_description TEXT
);

CREATE TABLE ThreatIntelligence (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(50),
    threat_type VARCHAR(50),
    threat_description TEXT
);

CREATE TABLE malware_events (
    event_id INT PRIMARY KEY,
    event_type VARCHAR(50),
    event_description TEXT,
    event_date TIMESTAMP
);

CREATE TABLE threat_actors (
    threat_actor_id INT PRIMARY KEY,
    threat_actor_name VARCHAR(100),
    threat_actor_organization VARCHAR(100),
    threat_actor_country VARCHAR(50)
);
