tables.table_schema:
CREATE TABLE vulnerabilities (
    vulnerability_id INT PRIMARY KEY,
    description TEXT,
    severity VARCHAR(20),
    timestamp TIMESTAMP
);

CREATE TABLE patches (
    patch_id INT PRIMARY KEY,
    patch_name VARCHAR(50),
    patch_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE systems (
    system_id INT PRIMARY KEY,
    system_name VARCHAR(50),
    system_type VARCHAR(50),
    last_checked TIMESTAMP
);

CREATE TABLE system_patches (
    system_patch_id INT PRIMARY KEY,
    system_id INT,
    patch_id INT,
    applied_timestamp TIMESTAMP
);

CREATE TABLE system_vulnerabilities (system_id INT REFERENCES systems(id),
vulnerability_id INT REFERENCES vulnerabilities(id),
PRIMARY KEY(system_id, vulnerability_id));

CREATE TABLE patch_vulnerabilities (patch_id INT REFERENCES patches(id),
vulnerability_id INT REFERENCES vulnerabilities(id),
PRIMARY KEY(patch_id, vulnerability_id));
