tables.table_schema:
CREATE TABLE wallets (
  id SERIAL PRIMARY KEY,
  user_id INT,
  wallet_name TEXT,
  private_key BYTEA
);

CREATE TABLE wallet_private_keys (
    id UUID PRIMARY KEY,
    wallet_name VARCHAR(50),
    private_key VARCHAR(256)
);

CREATE TABLE wallet_transactions (
    transaction_id UUID PRIMARY KEY,
    wallet_id UUID,
    transaction_date TIMESTAMP,
    amount DECIMAL(18, 8)
);

CREATE TABLE wallet_keys (
   id SERIAL PRIMARY KEY,
   user_id INT NOT NULL,
   encrypted_private_key BYTEA NOT NULL,
   created_at TIMESTAMP NOT NULL
);

CREATE TABLE users (
   id SERIAL PRIMARY KEY,
   username VARCHAR(50) NOT NULL,
   email VARCHAR(100) NOT NULL,
   password_hash BYTEA NOT NULL
);

CREATE TABLE wallet_accounts (
  id INT PRIMARY KEY,
  user_id INT,
  wallet_address VARCHAR(50),
  private_key VARCHAR(200)
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE wallet_encryption (
  wallet_address VARCHAR(50) PRIMARY KEY,
  encryption_key VARCHAR(200)
);

CREATE TABLE transactions (
  transaction_id INT PRIMARY KEY,
  wallet_address VARCHAR(50),
  amount DECIMAL(15, 2),
  transaction_date TIMESTAMP
);

CREATE TABLE wallet_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE wallet_addresses (
    address_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    address VARCHAR(100) NOT NULL
);

CREATE TABLE wallet_keys (
    key_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    key_data VARCHAR(200) NOT NULL
);

CREATE TABLE wallet_transactions (
    transaction_id INT PRIMARY KEY,
    sender_address VARCHAR(100) NOT NULL,
    receiver_address VARCHAR(100) NOT NULL,
    amount DECIMAL(18, 2) NOT NULL
);

CREATE TABLE wallet_logs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    log_details TEXT NOT NULL
);
