tables.table_schema:
CREATE TABLE user_accounts (
    user_id INT PRIMARY KEY,
    account_name VARCHAR(100),
    account_balance DECIMAL(18, 2),
    created_at TIMESTAMP
);

CREATE TABLE blockchain_addresses (
  address_id INT PRIMARY KEY,
  address VARCHAR(255),
  owner_name VARCHAR(255),
  created_timestamp TIMESTAMP
);

CREATE TABLE blockchain_audit_logs (
  log_id INT PRIMARY KEY,
  transaction_id INT,
  action VARCHAR(50),
  audit_timestamp TIMESTAMP
);

CREATE TABLE blockchain_addresses (
    address_id UUID PRIMARY KEY,
    address VARCHAR(255),
    balance DECIMAL(10,2),
    last_transaction_timestamp TIMESTAMP
);

CREATE TABLE blockchain_miners (
    miner_id UUID PRIMARY KEY,
    miner_name VARCHAR(255),
    mining_power DECIMAL(10,2),
    mining_reward DECIMAL(10,2)
);

CREATE TABLE blockchain_audit_log (
    log_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    event_type VARCHAR(50),
    event_description TEXT
);
