tables.table_schema:
CREATE TABLE suspicious_activities (
    activity_id INT PRIMARY KEY,
    activity_type VARCHAR(50),
    activity_description TEXT,
    activity_timestamp TIMESTAMP
);

CREATE TABLE suspicious_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_amount DECIMAL(10, 2),
    transaction_timestamp TIMESTAMP,
    transaction_status VARCHAR(20)
);

CREATE TABLE security_logs (
    log_id INT PRIMARY KEY,
    log_date DATE,
    log_time TIME,
    log_message TEXT
);

CREATE TABLE monitoring_agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(50),
    agent_status VARCHAR(20),
    agent_location VARCHAR(100)
);

CREATE TABLE SuspiciousActivities (
  activity_id SERIAL PRIMARY KEY,
  activity_description TEXT,
  activity_date TIMESTAMP,
  activity_type VARCHAR(50)
);

CREATE TABLE Suspects (
  suspect_id SERIAL PRIMARY KEY,
  suspect_name VARCHAR(100),
  suspect_description TEXT,
  investigation_status VARCHAR(50)
);

CREATE TABLE Alerts (
  alert_id SERIAL PRIMARY KEY,
  alert_message TEXT,
  alert_date TIMESTAMP,
  alert_level VARCHAR(20)
);
