    id UUID PRIMARY KEY,
    device_id UUID NOT NULL,
    activity_type VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE SecurityEvents (
    id UUID PRIMARY KEY,
    event_type VARCHAR(50) NOT NULL,
    event_description TEXT,
    event_time TIMESTAMP NOT NULL
);

