tables.table_schema:
CREATE TABLE firmware_updates (
    id SERIAL PRIMARY KEY,
    device_id INT NOT NULL,
    update_version VARCHAR(50) NOT NULL,
    update_date DATE NOT NULL
);

CREATE TABLE firmware_updates (
    id SERIAL PRIMARY KEY,
    device_id INT NOT NULL,
    update_version TEXT NOT NULL,
    update_date TIMESTAMP NOT NULL
);

CREATE TABLE device_inventory (
    device_id SERIAL PRIMARY KEY,
    device_name TEXT NOT NULL,
    firmware_version TEXT NOT NULL,
    last_update_date TIMESTAMP
);

CREATE TABLE patch_management (
    patch_id SERIAL PRIMARY KEY,
    patch_version TEXT NOT NULL,
    patch_description TEXT,
    release_date TIMESTAMP NOT NULL
);

CREATE TABLE compliance_status (
    status_id SERIAL PRIMARY KEY,
    device_id INT NOT NULL,
    patch_id INT NOT NULL,
    compliance_status TEXT NOT NULL
);

CREATE TABLE patches (
   patch_id serial PRIMARY KEY,
   patch_version VARCHAR(10) NOT NULL,
   description TEXT,
   release_date DATE NOT NULL
);

CREATE TABLE device_patches (
   device_id INT,
   patch_id INT,
   applied_date DATE,
   PRIMARY KEY (device_id, patch_id)
);
