tables.table_schema:
CREATE TABLE encryption_keys (
    id INT PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
    encryption_key VARCHAR(100) NOT NULL
);

CREATE TABLE certificates (
    cert_id INT PRIMARY KEY,
    device_id INT NOT NULL,
    certificate TEXT NOT NULL,
    validity_period TIMESTAMP
);

CREATE TABLE encryption_keys (
    id INT PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
    key_value TEXT NOT NULL,
    device_id INT
);

CREATE TABLE communication_certificates (
    certificate_id INT PRIMARY KEY,
    certificate_name VARCHAR(50) NOT NULL,
    certificate_type VARCHAR(20) NOT NULL,
    certificate_value TEXT NOT NULL
);

CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    device_type VARCHAR(20) NOT NULL,
    device_status VARCHAR(10) NOT NULL
);

CREATE TABLE certificates (
  id SERIAL PRIMARY KEY,
  certificate_name VARCHAR(50) NOT NULL,
  certificate_value TEXT NOT NULL,
  expiration_date DATE NOT NULL
);

CREATE TABLE communication_logs (
  id SERIAL PRIMARY KEY,
  device_id INT NOT NULL,
  timestamp TIMESTAMP NOT NULL,
  log_message TEXT
);

CREATE TABLE devices ( 
    device_id INT PRIMARY KEY, 
    device_name VARCHAR(100), 
    device_type VARCHAR(50), 
    registered_at TIMESTAMP 
);

CREATE TABLE device_keys ( 
    key_id INT, 
    device_id INT, 
    PRIMARY KEY (key_id, device_id) 
);

CREATE TABLE cert_device_mapping ( 
    cert_id INT, 
    device_id INT, 
    PRIMARY KEY (cert_id, device_id) 
);
