tables.table_schema:
CREATE TABLE IoT_Device_Data (
    device_id INT,
    data_timestamp TIMESTAMP,
    sensor_value FLOAT,
    anomaly_status BOOLEAN
);

CREATE TABLE Cybersecurity_Issues (
    issue_id INT,
    device_id INT,
    issue_type VARCHAR(50),
    issue_description TEXT
);

CREATE TABLE iot_devices (
    device_id INT PRIMARY KEY,
    device_name TEXT,
    device_type TEXT,
    location TEXT
);
