tables.table_schema:
CREATE TABLE communication_records (
    record_id INT PRIMARY KEY,
    sender_id INT NOT NULL,
    receiver_id INT NOT NULL,
    message TEXT NOT NULL
);

CREATE TABLE social_engineers (
    engineer_id INT PRIMARY KEY,
    engineer_name VARCHAR(50) NOT NULL
);

CREATE TABLE manipulation_opportunities (
    opportunity_id INT PRIMARY KEY,
    record_id INT NOT NULL,
    engineer_id INT NOT NULL,
    opportunity_description TEXT NOT NULL
);

CREATE TABLE SenderInformation (
    sender_id INT PRIMARY KEY,
    sender_name VARCHAR(50),
    sender_email VARCHAR(50),
    sender_phone VARCHAR(15)
);

CREATE TABLE ReceiverInformation (
    receiver_id INT PRIMARY KEY,
    receiver_name VARCHAR(50),
    receiver_email VARCHAR(50),
    receiver_phone VARCHAR(15)
);
