CREATE TABLE blackmail_records (
 id INT PRIMARY KEY,
 target_name TEXT,
 record TEXT
);

CREATE TABLE blackmail_targets (
    target_id INT PRIMARY KEY,
    target_name VARCHAR(50) NOT NULL,
    compromising_info TEXT,
    blackmailer_id INT
);

CREATE TABLE blackmailer (
    blackmailer_id INT PRIMARY KEY,
    blackmailer_name VARCHAR(50) NOT NULL,
    blackmail_method VARCHAR(100) NOT NULL,
    risk_level INT
);

CREATE TABLE blackmail_logs (
    log_id INT PRIMARY KEY,
    log_timestamp TIMESTAMP NOT NULL,
    event_description TEXT
);

CREATE TABLE blackmail_targets (
    target_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE compromising_info (
    info_id INT PRIMARY KEY,
    target_id INT,
    data TEXT,
    FOREIGN KEY (target_id) REFERENCES blackmail_targets(target_id)
);

CREATE TABLE threats (
    threat_id INT PRIMARY KEY,
    target_id INT,
    threat_message TEXT,
    FOREIGN KEY (target_id) REFERENCES blackmail_targets(target_id)
);

CREATE TABLE compliance_records (
    record_id INT PRIMARY KEY,
    target_id INT,
    compliance_status BOOLEAN,
    FOREIGN KEY (target_id) REFERENCES blackmail_targets(target_id)
);

CREATE TABLE blackmailers (
    blackmailer_id INT PRIMARY KEY,
    name VARCHAR(50),
    organization VARCHAR(100),
    contact_info TEXT
);
