tables.table_schema:
CREATE TABLE compliance_info (
    id INT PRIMARY KEY,
    company_name TEXT,
    audit_date DATE
);

CREATE TABLE network_security (
    id INT PRIMARY KEY,
    device_name TEXT,
    ip_address VARCHAR(15),
    status ENUM ('secure', 'vulnerable')
);

CREATE TABLE compliance_info (
    id INT PRIMARY KEY,
    department TEXT,
    regulation TEXT,
    compliance_status TEXT
);

CREATE TABLE security_events (
    event_id INT PRIMARY KEY,
    event_timestamp TIMESTAMP,
    event_type TEXT,
    event_description TEXT
);

CREATE TABLE compliance_information (
  id INT PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  regulatory_compliance_status VARCHAR(50) NOT NULL,
  audit_date DATE
);

CREATE TABLE security_controls (
  control_id INT PRIMARY KEY,
  control_name VARCHAR(100) NOT NULL,
  control_description TEXT NOT NULL,
  compliance_status BOOLEAN NOT NULL
);

CREATE TABLE compliance_info (
    id INT PRIMARY KEY,
    compliance_name VARCHAR(255),
    compliance_description TEXT,
    compliance_score FLOAT
);

CREATE TABLE audit_log (
    audit_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(100),
    timestamp TIMESTAMP
);

CREATE TABLE network_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_type VARCHAR(100),
    last_audit_timestamp TIMESTAMP
);

CREATE TABLE user_roles (
    user_id INT,
    role_id INT,
    PRIMARY KEY (user_id, role_id)
);
