tables.table_schema:
CREATE TABLE phishing_emails (
    id INT PRIMARY KEY,
    email_address VARCHAR(255),
    ip_address VARCHAR(15),
    fall_for_phishing BOOL
);

CREATE TABLE ip_addresses (
    id INT PRIMARY KEY,
    ip_address VARCHAR(15),
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE phishing_emails (
    id INT PRIMARY KEY,
    email_address VARCHAR(255),
    ip_address VARCHAR(15)
);

CREATE TABLE phishing_attempts (
    id INT PRIMARY KEY,
    email_id INT,
    timestamp TIMESTAMP,
    success BOOLEAN
);

CREATE TABLE exploitation_logs (
    id INT PRIMARY KEY,
    email_id INT,
    ip_address VARCHAR(15),
    exploitation_details TEXT
);

CREATE TABLE user_info (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email_address VARCHAR(100),
    ip_address VARCHAR(15)
);

CREATE TABLE phishing_emails (
    email_id INT PRIMARY KEY,
    email_subject VARCHAR(100),
    email_content TEXT,
    phishing_url VARCHAR(255)
);

CREATE TABLE user_ips ( 
    ip_id SERIAL PRIMARY KEY, 
    user_id INT NOT NULL, 
    ip_address INET NOT NULL, 
    timestamp TIMESTAMP NOT NULL 
);

CREATE TABLE exploit_methods ( 
    method_id SERIAL PRIMARY KEY, 
    method_name VARCHAR(255) NOT NULL, 
    description TEXT NOT NULL 
);
